            <p>Bienvenue sur la page du pouvoir</p>

            <a href=\"{{ path(\"admin_users\") }}\">
                <figure>
                    <img src=\"images/user.png\" class=\"admin-actions\">
                    <figcaption>Gerer les utilisateurs</figcaption>
                </figure>
            </a>
            <a href=\"\">
                <figure>
                    <img src=\"images/battleship.png\" class=\"admin-actions\">
                    <figcaption>Historique des parties</figcaption>
                </figure>
            </a>

        </div>
